mapper.update:
UPDATE HOT6_BOARD
SET BOARD_TITLE = #{boardTitle}, BOARD_CONTENT = #{boardContent}, BOARD_UPDATE_DATE = SYSDATE
WHERE BOARD_NUMBER = #{boardNumber}

mapper.select:
SELECT BOARD_NUMBER, BOARD_TITLE, BOARD_CONTENT, BOARD_DATE, BOARD_UPDATE_DATE, BOARD_TYPE, USER_NUMBER
FROM HOT6_BOARD
WHERE BOARD_NUMBER = #{boardNumber}
